mapper.select:
SELECT CAST(id AS CHAR) AS id, CAST(driver_id AS CHAR) AS driverId, CAST(customer_id AS CHAR) AS customerId, rate, remark, `status`, instance_id AS instanceId, DATE_FORMAT(create_time, '%Y-%m-%d %H:%i:%s') AS createTime FROM tb_order_comment WHERE order_id = #{orderId}
AND driver_id = #{driverId}
AND customer_id = #{customerId}
